mapper.select:
select *
from ap_static where groupid=#{groupid} and accountid in (0,
#{accountid})

SELECT pid,sta.id productId,`name`,f.id factorId,pollutantId,factor,tradeid,isFollow
FROM ap_static sta
LEFT JOIN ap_factor f ON f.productId = sta.id and f.typeid = 1 AND f.tradeid = #{tradeId} AND formulaId = #{formulaId}
WHERE sta.id >= 2000 AND pid = #{pid}


select DISTINCT `name`
from ap_static where groupid=#{groupid} and accountid in (0,
#{accountid})
