mapper.select:
SELECT c.districtName AS cityName,d.districtName AS townName,b.pollutantName,a.months, (select `name` from ap_static where id=stattype2)stattype, a.stattype3,a.statvalue,a.stat_exp,a.stat_value,a.stat_valtype FROM ap_stat_gov a LEFT JOIN ap_pollutant b ON a.pollutantId=b.id LEFT JOIN ap_district c ON a.city=c.id LEFT JOIN ap_district d ON a.town=d.id
a.fillyear=#{year}
and (a.province = #{did} OR a.city = #{did} or a.town = #{did})
and a.stattype=#{tablename}

SELECT c.districtName AS cityName,d.districtName AS townName,b.pollutantName,a.months, (select `name` from ap_static where id=stattype2)stattype, a.stattype3,a.statvalue,a.stat_exp,a.stat_value,a.stat_valtype FROM ap_stat_gov_pc a LEFT JOIN ap_pollutant b ON a.pollutantId=b.id LEFT JOIN ap_district c ON a.city=c.id LEFT JOIN ap_district d ON a.town=d.id
a.fillyear=#{year}
and a.city=#{did}
and a.stattype=#{tablename}

SELECT c.districtName AS cityName,d.districtName AS townName,b.pollutantName, (select `name` from ap_static where id=vehiclemodel)stattype3, a.statvalue,a.stat_exp,a.stat_value,a.stat_valtype FROM ap_stat_motor a LEFT JOIN ap_pollutant b ON a.pollutantId=b.id LEFT JOIN ap_district c ON a.city=c.id LEFT JOIN ap_district d ON a.town=d.id
a.fillyear=#{year}
and a.city=#{did}

