mapper.select:
select (select districtName from ap_district where id=e.province)as districtName1, (select districtName from ap_district where id=e.city)as districtName2, (select districtName from ap_district where id=e.town)as districtName3, if(e.trade3=0,(select tradeName from ap_trade where id=e.trade2), (if(e.trade4=0,(select tradeName from ap_trade where id=e.trade3), (select tradeName from ap_trade where id=e.trade4))))as tradeName, if(e.trade3=0,(select tradeNo from ap_trade where id=e.trade2),(if(e.trade4=0,(select tradeNo from ap_trade where id=e.trade3),(select tradeNo from ap_trade where id=e.trade4))))as tradeNo, e.enterpriceName as companyName,e.e_point,e.n_point, (select name from ap_static where id=(select deviceTypeId2 from ap_devices where id=s.deviceid))as deviceName, (select pollutantName from ap_pollutant where id=s.pollutantId)as pollutantName, (select name from ap_static where id=s.fuelid)as fuelName,s.statmonth month,s.statvalue,s.stat_exp,s.stat_factor,s.stat_valtype,s.stat_value,s.stat_dsrate from ap_stat_device s left join ap_enterprise e on e.id=s.companyid left join ap_devices d on d.id=s.deviceid
e.trade1=#{tradeid1}
and e.trade2=#{tradeid2}
and e.province=#{province}
and e.city=#{cityid}
and e.town=#{townid}
and e.enterpriceName like CONCAT('%',#{companyName},'%')
and d.deviceSerial like CONCAT('%',#{deviceSerial},'%')
and s.pollutantId=#{pollutantId}
and s.fillyear=#{fillyear}
and s.statmonth=#{month}
order by companyName,deviceName,pollutantName,month

select (select districtName from ap_district where id=e.province)as districtName1, (select districtName from ap_district where id=e.city)as districtName2, (select districtName from ap_district where id=e.town)as districtName3, if(e.trade3=0,(select tradeName from ap_trade where id=e.trade2),(if(e.trade4=0,(select tradeName from ap_trade where id=e.trade3),(select tradeName from ap_trade where id=e.trade4))))as tradeName, if(e.trade3=0,(select tradeNo from ap_trade where id=e.trade2),(if(e.trade4=0,(select tradeNo from ap_trade where id=e.trade3),(select tradeNo from ap_trade where id=e.trade4))))as tradeNo, e.enterpriceName as companyName,e.e_point,e.n_point, (select productName from ap_product where id=s.productid)as productName, (select pollutantName from ap_pollutant where id=s.pollutantId)as pollutantName, (select name from ap_static where id=s.fuelid)as fuelName, (select materialName from ap_material where id=s.materialid)as materialName,s.statmonth month,s.statvalue,s.stat_exp,s.stat_factor,s.stat_valtype,s.stat_value,s.stat_dsrate from ap_stat_product s left join ap_enterprise e on e.id=s.companyid left join ap_product p on p.id=s.productid
e.trade1=#{tradeid1}
and e.trade2=#{tradeid2}
and e.trade3=#{tradeid3}
and e.trade4=#{tradeid4}
and e.province=#{province}
and e.city=#{cityid}
and e.town=#{townid}
and e.enterpriceName like CONCAT('%',#{companyName},'%')
and p.productName like CONCAT('%',#{productName},'%')
and s.pollutantId=#{pollutantId}
and s.fillyear=#{fillyear}
and s.statmonth=#{month}
order by companyName,productName,pollutantName,month
